// CONFIGURACI√ìN
const MAX_OFERTAS_POR_DIA = 15;

// Palabras clave para filtrar ofertas
const keywords = ['remoto', 'latinoam√©rica', 'desarrollador', 'full stack', 'javascript', 'node.js', 'python', 'frontend', 'backend', 'web developer', 'software engineer'];

// Obtener todos los items del merge
const allItems = $input.all();
console.log('Total items recibidos:', allItems.length);

// Separar datos de API y datos del Sheet
let apiJobs = [];
let existingUrls = new Set();
let currentOffset = 0;

// Procesar cada item para identificar si viene de la API o del Sheet
allItems.forEach((item, index) => {
  console.log(`Item ${index}:`, Object.keys(item.json || {}));

  // Si el item tiene estructura de RemoteOK API (array con jobs)
  if (Array.isArray(item.json)) {
    console.log('Encontrados datos de API RemoteOK');
    // RemoteOK devuelve metadata en el primer elemento, jobs despu√©s
    apiJobs = item.json.slice(1);
  }
  // Si el item tiene estructura de Google Sheets (Empresa, Cargo, URL)
  else if (item.json && (item.json.Empresa || item.json.URL || item.json.A || item.json.C)) {
    console.log('Encontrados datos de Google Sheets');
    const url = item.json.URL || item.json.C || item.json['2'] || '';
    if (url && typeof url === 'string' && url.trim()) {
      existingUrls.add(url.trim());
    }
  }
  // Si el item parece ser un job individual de la API
  else if (item.json && (item.json.company || item.json.position || item.json.url)) {
    console.log('Encontrado job individual de API');
    apiJobs.push(item.json);
  }
  // Buscar el offset actual en los datos del sheet
  else if (item.json && item.json.OFFSET !== undefined) {
    currentOffset = parseInt(item.json.OFFSET) || 0;
    console.log(`Offset actual encontrado: ${currentOffset}`);
  }
});

console.log(`Jobs de API encontrados: ${apiJobs.length}`);
console.log(`URLs existentes en Sheet: ${existingUrls.size}`);
console.log(`Offset actual: ${currentOffset}`);

// Si no hay jobs de la API, intentar obtenerlos del primer item
if (apiJobs.length === 0 && allItems.length > 0) {
  const firstItem = allItems[0];
  if (firstItem.json && Array.isArray(firstItem.json)) {
    apiJobs = firstItem.json.slice(1);
    console.log(`Jobs obtenidos del primer item: ${apiJobs.length}`);
  }
}

// Filtrar ofertas nuevas que coincidan con palabras clave
const allFilteredJobs = apiJobs
  .filter(job => {
    if (!job || typeof job !== 'object') return false;

    const title = (job.position || job.title || '').toLowerCase();
    const description = (job.description || '').toLowerCase();
    const company = (job.company || '').toLowerCase();
    const tags = Array.isArray(job.tags) ? job.tags.join(' ').toLowerCase() : '';
    const url = job.url || '';

    // Verificar que tenga URL v√°lida
    if (!url || !url.trim()) return false;

    // Verificar que no est√© ya en el sheet
    if (existingUrls.size > 0 && existingUrls.has(url.trim())) {
      console.log(`URL duplicada encontrada: ${url}`);
      return false;
    }

    // Verificar palabras clave
    const searchText = `${title} ${description} ${company} ${tags}`;
    const hasKeyword = keywords.some(keyword => searchText.includes(keyword));

    if (hasKeyword) {
      console.log(`Oferta v√°lida encontrada: ${company} - ${title}`);
    }

    return hasKeyword;
  });

console.log(`Total ofertas v√°lidas encontradas: ${allFilteredJobs.length}`);

// APLICAR PAGINACI√ìN: Tomar las siguientes 15 ofertas seg√∫n el offset
const startIndex = currentOffset;
const endIndex = currentOffset + MAX_OFERTAS_POR_DIA;
const paginatedJobs = allFilteredJobs.slice(startIndex, endIndex);

console.log(`Aplicando paginaci√≥n: ${startIndex} a ${endIndex}`);
console.log(`Ofertas para esta ejecuci√≥n: ${paginatedJobs.length}`);

// Calcular el nuevo offset para la pr√≥xima ejecuci√≥n
const newOffset = endIndex;
const hasMoreJobs = newOffset < allFilteredJobs.length;

console.log(`Nuevo offset para pr√≥xima ejecuci√≥n: ${newOffset}`);
console.log(`¬øHay m√°s ofertas disponibles?: ${hasMoreJobs}`);

// Si no hay ofertas en esta p√°gina, reiniciar el offset
if (paginatedJobs.length === 0) {
  console.log('No hay ofertas en esta p√°gina, reiniciando offset a 0');
  return [{ json: { 
    sinOfertas: true, 
    mensaje: `No hay m√°s ofertas en la p√°gina ${Math.floor(currentOffset / MAX_OFERTAS_POR_DIA) + 1}. Reiniciando b√∫squeda.`, 
    totalProcesadas: apiJobs.length,
    totalFiltradas: allFilteredJobs.length,
    urlsExistentes: existingUrls.size,
    maxOfertas: MAX_OFERTAS_POR_DIA,
    offsetActual: currentOffset,
    nuevoOffset: 0, // Reiniciar
    paginaActual: Math.floor(currentOffset / MAX_OFERTAS_POR_DIA) + 1,
    // Datos especiales para actualizar el offset en el sheet
    OFFSET: 0,
    ESTADO: 'REINICIADO'
  }}];
}

// Preparar las ofertas con informaci√≥n de paginaci√≥n
const finalJobs = paginatedJobs.map(job => ({
  json: {
    Empresa: job.company || 'No especificada',
    Cargo: job.position || job.title || 'No especificado',
    URL: job.url || ''
  }
}));

// Agregar informaci√≥n del nuevo offset al final
finalJobs.push({
  json: {
    OFFSET: newOffset,
    ESTADO: hasMoreJobs ? 'CONTINUAR' : 'REINICIAR_SIGUIENTE',
    PAGINA: Math.floor(currentOffset / MAX_OFERTAS_POR_DIA) + 1,
    TOTAL_DISPONIBLES: allFilteredJobs.length,
    // Datos informativos
  }
});



return finalJobs;

// Este nodo maneja AMBOS casos: con ofertas y sin ofertas
const inputData = $input.all();
console.log('Datos recibidos en generar HTML:', inputData.length);

// Verificar si es el caso sin ofertas
if (inputData.length === 1 && inputData[0].json && inputData[0].json.sinOfertas) {
  const totalProcesadas = inputData[0].json.totalProcesadas || 0;
  const urlsExistentes = inputData[0].json.urlsExistentes || 0;

  const html = `
    <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto; padding: 20px;">
      <div style="background: linear-gradient(135deg, #ff9a56 0%, #ff6b6b 100%); color: white; padding: 20px; text-align: center; border-radius: 10px 10px 0 0;">
        <h1 style="margin: 0; font-size: 24px;">üîç B√∫squeda Diaria de Ofertas</h1>
      </div>
      <div style="background: #f8f9fa; padding: 30px; text-align: center; border-radius: 0 0 10px 10px; border: 1px solid #e9ecef;">
        <div style="font-size: 48px; margin-bottom: 20px;">üò¥</div>
        <h2 style="color: #6c757d; margin-bottom: 15px;">No hay nuevas ofertas hoy</h2>
        <p style="color: #868e96; font-size: 16px; line-height: 1.5;">
          Se procesaron <strong>${totalProcesadas}</strong> ofertas de RemoteOK.<br>
          Tienes <strong>${urlsExistentes}</strong> ofertas ya guardadas.<br>
          Ninguna nueva coincide con tus criterios.
        </p>
        <div style="margin-top: 25px; padding: 15px; background: #fff3cd; border-radius: 5px; border-left: 4px solid #ffc107;">
          <p style="margin: 0; color: #856404; font-size: 14px;">üí° El sistema seguir√° buscando autom√°ticamente ma√±ana a las 8:00 AM</p>
        </div>
      </div>
      <div style="text-align: center; margin-top: 20px; color: #adb5bd; font-size: 12px;">
        <p>Automatizado con ‚ù§Ô∏è por n8n | ${new Date().toLocaleDateString('es-ES')}</p>
      </div>
    </div>
  `;

  return [{ json: { 
    html, 
    subject: 'üîç B√∫squeda Diaria - Sin nuevas ofertas',
    hasOfertas: false
  }}];
}

// Si llegamos aqu√≠, hay ofertas nuevas
const ofertas = inputData;
const totalOfertas = ofertas.length;

console.log(`Generando HTML para ${totalOfertas} ofertas`);

const filas = ofertas.map((item, index) => {
  const empresa = item.json.Empresa || 'No especificada';
  const cargo = item.json.Cargo || 'No especificado';
  const url = item.json.URL || '#';

  return `
    <tr style="${index % 2 === 0 ? 'background-color: #f8f9fa;' : 'background-color: white;'}">
      <td style="padding: 12px; text-align: center; font-weight: bold; color: #495057;">${index + 1}</td>
      <td style="padding: 12px; color: #212529;">${empresa}</td>
      <td style="padding: 12px; color: #212529;">${cargo}</td>
      <td style="padding: 12px; text-align: center;">
        <a href="${url}" target="_blank" style="background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 8px 16px; text-decoration: none; border-radius: 20px; font-size: 12px; font-weight: bold; display: inline-block;">VER OFERTA</a>
      </td>
    </tr>
  `;
}).join('');

const html = `
  <div style="font-family: Arial, sans-serif; max-width: 700px; margin: 0 auto; padding: 20px;">
    <div style="background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 20px; text-align: center; border-radius: 10px 10px 0 0;">
      <h1 style="margin: 0; font-size: 24px;">üéØ ¬°Nuevas Ofertas Encontradas!</h1>
      <p style="margin: 10px 0 0 0; opacity: 0.9;">${totalOfertas} nueva${totalOfertas !== 1 ? 's' : ''} oportunidad${totalOfertas !== 1 ? 'es' : ''} para ti</p>
    </div>

    <div style="background: white; border: 1px solid #e9ecef; border-top: none;">
      <table style="width: 100%; border-collapse: collapse;">
        <thead>
          <tr style="background: #343a40; color: white;">
            <th style="padding: 15px; text-align: center; font-weight: bold;">#</th>
            <th style="padding: 15px; text-align: left; font-weight: bold;">Empresa</th>
            <th style="padding: 15px; text-align: left; font-weight: bold;">Cargo</th>
            <th style="padding: 15px; text-align: center; font-weight: bold;">Acci√≥n</th>
          </tr>
        </thead>
        <tbody>
          ${filas}
        </tbody>
      </table>
    </div>

    <div style="background: #d4edda; padding: 20px; border-radius: 0 0 10px 10px; border: 1px solid #c3e6cb; border-top: none;">
      <div style="text-align: center;">
        <p style="margin: 0; color: #155724; font-weight: bold;">‚úÖ ${totalOfertas} nueva${totalOfertas !== 1 ? 's' : ''} oferta${totalOfertas !== 1 ? 's' : ''} guardada${totalOfertas !== 1 ? 's' : ''} en tu Google Sheet</p>
        <p style="margin: 5px 0 0 0; color: #155724; font-size: 14px;">¬°No olvides revisar y postularte a las que te interesen!</p>
      </div>
    </div>

    <div style="text-align: center; margin-top: 20px; color: #adb5bd; font-size: 12px;">
      <p>Automatizado con ‚ù§Ô∏è por n8n | ${new Date().toLocaleDateString('es-ES', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</p>
    </div>
  </div>
`;

return [{ json: { 
  html, 
  subject: `üéØ ${totalOfertas} Nueva${totalOfertas !== 1 ? 's' : ''} Oferta${totalOfertas !== 1 ? 's' : ''} de Trabajo`,
  hasOfertas: true
}}];
